mapper.select:
SELECT COUNT(*)
FROM admin
WHERE 1=1
AND adminid = #{adminid }
AND adminpw = #{adminpw }

SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT userno, usertype, username, userid, usernick, userphone, permit, follow, sociallogin
		FROM user_table
		WHERE userno >=1
		ORDER BY userno
	) B
	ORDER BY rnum
) BOARD
WHERE rnum BETWEEN #{startNo} AND #{endNo}
