mapper.select:
 SELECT userno FROM user_table
 WHERE 1=1
	AND usernick = #{usernick}

mapper.update:
UPDATE user_table SET prwritedate = TO_CHAR(SYSDATE, 'YYYY.MM.DD HH24:MI:SS')
WHERE 1=1
	AND userno = #{userNo}

mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT BO.boardno, BO.title, BO.writtendate, BO.contents, BO.views, BO.userno, BO.postno, P.prname, U.usernick FROM board BO, prtype P, user_table U WHERE BO.boardno = P.boardno AND BO.userno = U.userno AND BO.postno = 2
AND ${search2.searchType} LIKE '%'||#{search2.search}||'%'
ORDER BY boardno DESC ) B ORDER BY rnum ) BOARD WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE board set views = views +1 
WHERE 1=1
	AND boardno = #{value }

mapper.select:
SELECT fileno, originname, storedname, filesize, boardno FROM upfile
WHERE boardno = ${value}
ORDER BY fileno
